import * as React from "react";
import { styles } from "./Measurements.styles";

import type dummy_measurements from "dummy_measurements.json";

interface MeasurementsProps {
  measurements: typeof dummy_measurements.data;
}

export const Measurements: React.FC<MeasurementsProps> = ({
  measurements,
}: MeasurementsProps) => {
  const [selectedMeasurement, setSelectedMeasurement] = React.useState<
    number | null
  >(null);

  const isSelected = (i: number) => {
    return selectedMeasurement === i;
  };

  return (
    <div className={styles.container}>
      {measurements.map((measurement, i) => {
        return (
          <div
            className={`${styles.measurement} ${selectedMeasurement === i}`}
            key={measurement.id}
            onClick={() => setSelectedMeasurement(i)}
          >
            <h2 className={styles.measurementTitle}>{measurement.title}</h2>
            <p className={styles.measurementSubtitle}>{measurement.subTitle}</p>
            <ul>
              {measurement.values.map((value) => (
                <li>{value}</li>
              ))}
            </ul>
          </div>
        );
      })}
    </div>
  );
};
