import { css } from "@emotion/css";
import * as React from "react";

import dummy_measurements from "dummy_measurements.json";

const styles = {
  container: css`
    top: 1rem;
    left: 1rem;
    position: absolute;
    display: grid;
    grid-template-columns: 1fr;
    grid-gap: 1rem;
    padding: 1rem;

    max-height: calc(100% - 3rem);
    overflow-y: auto;

    /*
    * Would use container query, as it is supported by all major browsers,
    * but it is not supported by emotion yet.
    */
    @media (min-width: 768px) {
      grid-template-columns: 1fr 1fr;
    }
  `,
  measurement: css`
    padding: 1rem;
    background: white;
    border-radius: 0.25rem;
    box-shadow: 0 0 0.5rem rgba(0, 0, 0, 0.3);
    transition: transform 0.2s ease-in-out;

    &:hover {
      transform: scale(1.05);
    }
  `,
};

interface MeasurementsProps {
  measurements: typeof dummy_measurements.data;
}

export const Measurements: React.FC<MeasurementsProps> = ({
  measurements,
}: MeasurementsProps) => {
  return (
    <div className={styles.container}>
      {measurements.map((measurement) => {
        return (
          <div className={styles.measurement} key={measurement.id}>
            <h2>{measurement.title}</h2>
            <p>{measurement.active}</p>
          </div>
        );
      })}
    </div>
  );
};
